import { HttpResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/auth.service';
import { Router } from '@angular/router';
import Swal from 'sweetalert2';
@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.scss']
})
export class LoginPageComponent implements OnInit {
  constructor (private authService: AuthService, private router: Router) {}

  ngOnInit(){
    
  }

  Toast = Swal.mixin({
    toast: true,
    position: 'top-end',
    showConfirmButton: false,
    timer: 3000,
    timerProgressBar: true,
    didOpen: (toast) => {
      toast.addEventListener('mouseenter', Swal.stopTimer)
      toast.addEventListener('mouseleave', Swal.resumeTimer)
    }
  })

  onLoginButtonClicked(email: string, password: string) {
    this.authService.login(email, password).subscribe((res: HttpResponse<any>) => {
      if (res.status === 200) {
        this.router.navigate(['/lists']);
        this.Toast.fire({
          icon: 'success',
          title: 'Logged in successfully'
        })

      }
      console.log(res);
    })
  }
}
<div class="centered-content">  
  
  <div class="white-box">

    <img src="assets/logo.jpeg" style="align-self: center; width: 100%;">

    <br><br>

    <h1 class="title has-text-primary" style="text-align: center;">Login</h1>
    
    <div class="field">
      <p class="control has-icons-left has-icons-right">
        <input #emailInput class="input has-background-light" type="email" placeholder="Email">
        <span class="icon is-small is-left">
          <i class="fas fa-envelope"></i>
        </span>
      </p>
    </div>
    <div class="field">
      <p class="control has-icons-left">
        <input #pwInput class="input has-background-light" type="password" placeholder="Password">
        <span class="icon is-small is-left">
          <i class="fas fa-lock"></i>
        </span>
      </p>
    </div>
    <div class="field" style="display: flex; justify-content: flex-end;">
      <p class="control">
        <button class="button is-success has-text-white" (click)="onLoginButtonClicked(emailInput.value, pwInput.value)" >
          Login
        </button>
      </p>
    </div>

    <br>

    <p class="has-text-grey" style="text-align: center;">Need an account? <a routerLink="/signup">Sign up</a> now!</p>

  </div>
