import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router, Params } from '@angular/router';
import { UserService } from 'src/app/user.service';

@Component({
  selector: 'app-change-email',
  templateUrl: './change-email.component.html',
  styleUrls: ['./change-email.component.scss']
})
export class ChangeEmailComponent implements OnInit {

  constructor(private route: ActivatedRoute, private userService: UserService, private router: Router) { }

  userId: string;

  ngOnInit() {
    this.route.params.subscribe(
      (params: Params) => {
        this.userId = params.userId;
        console.log(params.userId);
      }
    )
  }

  changeUserEmail(title: string) {
    this.userService.changeUserEmail(this.userId, title).subscribe((res: any) => {
      this.router.navigate(['/admin']);
      console.log("Email changed successfully");
    })
  }

}
<div class="centered-content">
    <div class="modal-box">
      <h1 class="title">
        Change user email
      </h1>
  
      <input #emailTitleInput class="input has-background-light is-medium" type="text" placeholder="Enter new email...">
      <br><br>
      <div class="buttons is-right">
        <button class="button is-medium" routerLink="/admin">Cancel</button>
        <button class="button is-primary has-text-white is-medium" (click)="changeUserEmail(emailTitleInput.value)">Save</button>
      </div>
  
      
    </div>
  </div>
